module o-ran-smo-teiv-ran {
yang-version 1.1;
namespace "urn:o-ran:smo-teiv-ran";
prefix or-teiv-ran;
import o-ran-smo-teiv-common-yang-types {prefix or-teiv-types; }
import o-ran-smo-teiv-common-yang-extensions {prefix or-teiv-yext; }
import _3gpp-common-yang-types { prefix types3gpp; }
organization "ORAN";
description
"Sample Model.";
revision "2024-05-24" {
  description "Initial revision."
  or-teiv-yext:label 0.1.0;
}
or-teiv-yext:domain RAN;
list GNBDUFunction {
  uses or-teiv-types:Top_Grp_Type;
  key id;
  container attributes {
    container dUpLMNId {
      uses types3gpp:PLMNId;
    }
    leaf gNBDUId {
      type uint32;
    }
    leaf gNBId {
      type uint32;
    }
    leaf gNBIdLength {
      type uint32;
    }
  }
}
list NRCellDU {
  uses or-teiv-types:Top_Grp_Type;
  key id;
  container attributes {
    leaf cellLocalId {
      type uint32;
    }
    leaf nCI {
      type uint32;
    }
    leaf nRPCI {
      type uint32;
    }
    leaf nRTAC {
      type uint32;
    }
  }
}
list NRSectorCarrier {
  uses or-teiv-types:Top_Grp_Type;
  key id;
    container attributes {
      leaf arfcnDL {
        type uint32;
      }
      leaf arfcnUL {
        type uint32;
      }
      leaf frequencyDL {
        type uint32;
      }
      leaf frequencyUL {
        type uint32;
      }
      leaf bSChannelBwDL {
        type uint32;
      }
    }
  }
}
